package outil;

public class Data {
	protected double x;
	protected double y;
	
	public Data() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Data(double x, double y) {
		super();
		this.x = x;
		this.y = y;
	}
	public double getX() {
		return x;
	}
	public void setX(double x) {
		this.x = x;
	}
	public double getY() {
		return y;
	}
	public void setY(double y) {
		this.y = y;
	}
	public String toString(){
		return "x:"+x+"\ty:"+y;
	}
	
}
